INSERT INTO `wp_news` (`news_id`, `user_id`, `title`, `content`, `thumb`, `status`, `stick`, `fine`, `create_time`, `update_time`, `commentnum`, `comment`) VALUES
(1, 1, '关于本站', '<p>\r\n	首次加载可能较慢，请耐心等待片刻即可。\r\n</p>\r\n<p>\r\n	1.对于首次访问本网站的用户，建议可以先将所有的钢琴键都按一遍，缓存声音数据，否则首次按键可能会有延迟，影响用户体验。\r\n</p>\r\n<p>\r\n	2.关于录音功能，录音功能对于浏览器要求较高，建议使用谷歌，火狐，ie10以上等支持HTML5的浏览器。\r\n</p>\r\n<p>\r\n	3.关于换肤功能，点击右侧style出现皮肤列表。针对不同用户的喜好有几种颜色可供选择，如有建议请联系管理员。\r\n</p>\r\n<p>\r\n	4.关于音乐播放功能，便于用户在浏览帖子的同时可以听音乐，欣赏钢琴之美(原来看帖子也是一种享受)。\r\n</p>\r\n<p>\r\n	5.关于曲谱站，收藏的曲谱将会出现在首页供用户弹奏，这么多曲谱总有一款适合你，现有刚快去注册弹奏钢琴吧！\r\n</p>', '/WebPiano/upload/2017/04/13/58ef290ac945b.jpg', 1, 1, 0, 1492068630, 0, 0, 0),
(2, 1, '网站首页高清背景图', '<p>\r\n	愿四月不再是一个谎言\r\n</p>\r\n<p>\r\n	<img src="/WebPiano/upload/2017/04/13/58ef5b436db83.jpg" alt="" />&nbsp;&nbsp;&nbsp;&nbsp;<img src="/WebPiano/upload/2017/04/13/58ef5b54d0e6d.jpg" alt="" />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src="/WebPiano/upload/2017/04/13/58ef5bd0bdd40.jpg" alt="" />&nbsp;&nbsp;&nbsp;&nbsp; <img src="/WebPiano/upload/2017/04/13/58ef5bf9cd164.jpg" alt="" />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img src="/WebPiano/upload/2017/04/13/58ef5be3e7ca3.jpg" alt="" /> \r\n</p>', '/WebPiano/upload/2017/04/13/58ef5b354b632.jpg', 1, 0, 1, 1492081518, 0, 0, 0),
(3, 1, '广告位招租', '嗯~（认真脸）<img src="http://120.77.80.37/WebPiano/Public/js/kindeditor/plugins/emoticons/images/41.gif" border="0" alt="" />', '', 1, 1, 0, 1492093896, 0, 0, 0),
(4, 1, '网站使用小技巧', '<p>\r\n	首页键盘处做对应的键表示简音，1 代表中音 1 ，1· 代表高音1，·1表示低音1，以此类推，后面点越多，数字多大，音调越高，反之亦然，不懂乐理的小伙伴不妨多试试吧。\r\n</p>\r\n<p>\r\n	首页的自动滚动可以帮助你在弹奏时自动帮你向下读谱，点击次数越多，滚动越快。当然新手可以根据自己的进度手动调整。\r\n</p>\r\n<p>\r\n	曲谱站中有两种曲谱，一种是单音的简谱，适合新手，一般一只手即可弹奏，极易上手。第二种是双手弹奏的正式曲谱，需要两只手的配合，就犹如真实的在弹钢琴般，不过想要弹好的话，不多加练习可是不行的。\r\n</p>\r\n<p>\r\n	右侧音乐栏首次播放需缓冲一下，每首音乐大小在3,4M左右，流量党慎用，土豪随意。\r\n</p>\r\n<p>\r\n	个人中心中可以修改资料，上传头像，查看过往记录等等。\r\n</p>', '/WebPiano/upload/2017/04/13/58ef8c37b2da5.jpg', 1, 1, 0, 1492094796, 0, 0, 0),
(5, 1, '双手曲谱具体说明', '第一大行最后开始弹奏，上行高音356需要用左手在小键盘快速按下，同时下行的46需要右手在主键盘同时按下，接着往后高音1和7再同时按下，后面以此类推。', '/WebPiano/upload/2017/04/17/58f466726e303.png', 1, 0, 0, 1492412278, 0, 0, 0),
(6, 1, '网站右侧音乐列表', '<p>\r\n	1.梦中的婚礼\r\n</p>\r\n<p>\r\n	2.菊次郎的夏天\r\n</p>\r\n<p>\r\n	3.夜的钢琴曲五\r\n</p>\r\n<p>\r\n	4.卡农\r\n</p>\r\n<p>\r\n	5.千与千寻主题曲\r\n</p>\r\n<p>\r\n	6.风居住的街道\r\n</p>\r\n<p>\r\n	7.穿越时空的思念\r\n</p>\r\n<p>\r\n	8.千本樱\r\n</p>\r\n<p>\r\n	9.数码宝贝主题曲\r\n</p>\r\n<p>\r\n	10.四月是你的谎言op\r\n</p>\r\n<p>\r\n	11.天空之城\r\n</p>\r\n<p>\r\n	12.未闻花名ed\r\n</p>\r\n<p>\r\n	剩下音乐会逐步添加，敬请期待\r\n</p>', '/WebPiano/upload/2017/04/17/58f467dc98266.jpg', 1, 0, 0, 1492412593, 0, 0, 0),
(7, 1, '四月是你的谎言经典台词', '<p>\r\n	或许前路永夜，即便如此我也要前进，因为星光即使微弱也会为我照亮前路。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; ————宫园薰&nbsp;\r\n</p>\r\n<p>\r\n	你驻足于春色之中，于那独一无二的春色之中。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; ————有马公生\r\n</p>\r\n<p>\r\n	莫扎特曾经说过大胆地踏上旅途吧，我不知道路途的前方究竟有什么，但是，我们还是迈出了步伐，我们仍在旅途之中。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; ————有马公生\r\n</p>', '/WebPiano/upload/2017/04/17/58f46dad03607.jpg', 1, 0, 0, 1492413873, 0, 0, 0),
(8, 1, '怎样解读《四月是你的谎言》的结局？', '<p>\r\n	<p>\r\n		<b>这并不是一部简单的励志漫画或恋爱漫画，而是一部真正意义上的少年漫画，而少年漫画的精髓永远是「成长」。</b><br />\r\n<b><br />\r\n明确了这点之后，私以为，这个结局是全篇的画龙点睛之笔。</b>\r\n	</p>\r\n	<p>\r\n		宫园薰的死并不意外，相信每位读者都有充分的心理准备。相比传统的大团圆结局而言，悲剧确实更适合这部作品。宫园薰的死从一开始早已注定，强行安排手术成功的结局也并不现实。她的死，一方面衬托并证明了有马公生的成长，表明了有马不会因为至亲之人的去世而再次陷入沉沦；一方面也为作品中的每个人，乃至作品外的我们注入了直面生活，努力前行的力量。\r\n	</p>\r\n	<p>\r\n		最终话以宫园薰在手术前写给有马公生的信为主体。信的内容无须详述，相信每个认真阅读过这部作品的人都能够多少猜出信的内容。我想强调的是，幼时的有马公生改变了幼时宫园薰的人生轨迹，而十四岁的宫园薰将十四岁的有马公生从即将堕落的人生边缘拉回，让他认识到了生活的多彩多姿。这何尝不是一种相互的扶持与拯救？从这点意义上来说，作品最终又回到了成长这个永恒的主题。\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</p>\r\n<p>\r\n	<p>\r\n		宫园薰的逝去与母亲的逝去是如此相似，却又那么不同：母亲的逝去使得有马公生无法继续钢琴之路，并一直活在母亲的阴影之中；宫园薰的逝去将有马公生的成长推向了高潮，使有马公生明白，即使失去了自己最亲爱的人，还始终有人陪伴着他，和他一起互相扶持，笑对明天，而不应当沉溺在过去的失落与惆怅中。<b>从这点意义上说，在故事的最后，有马公生从一个男孩子成长为一个真正的男人。</b>\r\n	</p>\r\n	<p>\r\n		<b><br />\r\n</b>\r\n	</p>\r\n<br />\r\n	<div>\r\n		<div>\r\n			<p>\r\n				当一个人穷尽生活的种种可能性，最终明白了身边的人对自己的期待与扶持，并选择用自己的行动回报他们时，成长就悄然来临了。\r\n			</p>\r\n			<p>\r\n				因此，如此说来，这又不仅仅是一部普通的少年漫画。它承载了太多太多的东西了：热血、励志、青春、友情、亲情……还有，最重要的：<b>对生活的热爱，对未来的坚信。</b>\r\n			</p>\r\n			<p>\r\n				从另一方面说，个人看来，这部作品里没有所谓的配角，每个人的出现都有不可忽视的意义。同样，在结局，每个人都获得了成长。有马公生与宫园薰无需多言，泽部椿学会正视自己的感情，向有马公生大胆表达了心意，同时也懂得了用自己的行动改变现实：努力学习考上了音大附近的高中，在演奏会上的咳嗽让有马公生完成了演奏。（某种程度上说，这也是对她努力的回报，同时，她也传承了宫园薰对有马公生成长的带动作用，后期的她与宫园薰何其相似）相座武士和井川绘见再度燃起斗志，重新踏上一段美妙的旅程。相座凪从兄控中毕业，学会了用正确的方式面对自己所喜爱的音乐、自己喜欢的人。（也就是说，有马公生对相座凪就如宫园薰对有马公生一样，有马公生将这种成长的影响传递了下去）濑户小春受有马公生的影响走上音乐之路……这么说来，所谓的成长并不局限于一条线，而是一个环，没有终点。若要说有起点，就是宫园薰啊。\r\n			</p>\r\n		</div>\r\n<br />\r\n		<div>\r\n			<div>\r\n				<p>\r\n					欧阳修有词：去年元夜时，花市灯如昼。月上柳梢头，人约黄昏后。今年元夜时，月与灯依旧。不见去年人，泪湿春衫袖。 所不同的是，天国的宫园薰不会允许有马公生再度陷入悲伤之中的。读到这里，我想，大家已经明白有马公生该如何继续在人生道路上坚强地走下去了，而宫园薰，在天堂也应当没有什么遗憾了。\r\n				</p>\r\n				<p>\r\n					所以，这并不是一部悲剧，而是一部正剧。这不是结束，而是名为结束的开始。\r\n				</p>\r\n				<p>\r\n					<b>实际上，这部作品所讲述的还是一个永恒的母题：追逐梦想的过程中留有遗憾的青春。</b>一般意义上讲，这确实是一个略显老套的故事，也存在着这样那样的不足，但是作者讲故事的手法，尤其是对细节的成功把握赋予了这部优秀作品独特的感染力。整篇故事无时无刻不在尽力描摹与铺陈青春之美，因此许多人可能会感到这个故事有些矫情，但是，这正是我喜欢这个故事的地方。\r\n				</p>\r\n				<p>\r\n					成长并非必然如此，却又本应如此。所谓一部少年漫画的内涵，就在这里。\r\n				</p>\r\n				<p>\r\n					如果要从整体来评价的话，那么，这部作品应当就是一曲有笑有泪的七彩青春赞歌。\r\n				</p>\r\n			</div>\r\n<br />\r\n作者：王晗<br />\r\n链接：https://www.zhihu.com/question/27966304/answer/38988065<br />\r\n来源：知乎<br />\r\n		</div>\r\n	</div>\r\n</p>\r\n<br />\r\n<br />', '/WebPiano/upload/2017/04/17/58f4b4f4668f1.jpg', 1, 0, 0, 1492432126, 0, 0, 0),
(9, 1, '四月是你的谎言高清竖屏壁纸', '<p>\r\n	这是一个会动的镇楼图\r\n</p>\r\n<p>\r\n	<img src="/WebPiano/upload/2017/04/17/58f4b81921e4f.jpg" alt="" />&nbsp;&nbsp;&nbsp;&nbsp;<img src="/WebPiano/upload/2017/04/17/58f4b8199fc78.jpg" alt="" />&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p>\r\n	<img src="/WebPiano/upload/2017/04/17/58f4b81a12a2b.jpg" alt="" />&nbsp;&nbsp;&nbsp;&nbsp;<img src="/WebPiano/upload/2017/04/17/58f4b81b1e146.jpg" alt="" />&nbsp;&nbsp;&nbsp;&nbsp;<img src="/WebPiano/upload/2017/04/17/58f4b81da768a.png" alt="" />\r\n</p>', '/WebPiano/upload/2017/04/17/58f4b6c040697.gif', 1, 0, 1, 1492432956, 0, 0, 0),
(10, 1, '《四月是你的谎言》女主写给男主的信', '<p>\r\n	谨以此纪念我逝去的小薰\r\n</p>\r\n<p>\r\n	<img src="/WebPiano/upload/2017/04/17/58f4d0c4c21c9.jpg" alt="" />\r\n</p>', '/WebPiano/upload/2017/04/17/58f4d097be4c0.png', 1, 0, 0, 1492439246, 0, 0, 0);
INSERT INTO `wp_news` (`news_id`, `user_id`, `title`, `content`, `thumb`, `status`, `stick`, `fine`, `create_time`, `update_time`, `commentnum`, `comment`) VALUES
(11, 1, '《四月是你的谎言》里有多少个谎言?', '<br />\r\n<p>\r\n	<b>一切谎言的开端</b><br />\r\n宫园的两个愿望：<br />\r\n<b>1、和有马公生合奏（已实现）</b><br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/b40a961e58a68d9529203e183f4c0d32_b.png" data-rawwidth="453" data-rawheight="218" class="origin_image zh-lightbox-thumb" width="453" data-original="https://pic3.zhimg.com/b40a961e58a68d9529203e183f4c0d32_r.png"&amp;amp;gt;<img src="https://pic3.zhimg.com/b40a961e58a68d9529203e183f4c0d32_b.png" class="origin_image zh-lightbox-thumb lazy" width="453" /><b>2、希望有马公生能成为幸福的钢琴家（同纮子，死后实现）<br />\r\n</b><br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/e5715ed190a7045851dc29472ed49a76_b.png" data-rawwidth="398" data-rawheight="662" class="content_image" width="398"&amp;amp;gt;<img src="https://pic3.zhimg.com/e5715ed190a7045851dc29472ed49a76_b.png" class="content_image lazy" width="398" /><br />\r\n———————————————————————————————————————————<br />\r\n<b>第一个谎言：喜欢渡亮太</b><br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/171005de8c63be7d8943797337525681_b.png" data-rawwidth="464" data-rawheight="329" class="origin_image zh-lightbox-thumb" width="464" data-original="https://pic2.zhimg.com/171005de8c63be7d8943797337525681_r.png"&amp;amp;gt;<img src="https://pic2.zhimg.com/171005de8c63be7d8943797337525681_b.png" class="origin_image zh-lightbox-thumb lazy" width="464" />&amp;amp;lt;img src="https://pic4.zhimg.com/fef6bfc9f8298079b4469a24545683a7_b.png" data-rawwidth="297" data-rawheight="212" class="content_image" width="297"&amp;amp;gt;结局：这个谎言其实早已被渡识破，渡早知道宫园和公生两情相悦，为了最好的朋友公生，渡一直装傻<img src="https://pic4.zhimg.com/fef6bfc9f8298079b4469a24545683a7_b.png" class="content_image lazy" width="297" />结局：这个谎言其实早已被渡识破，渡早知道宫园和公生两情相悦，为了最好的朋友公生，渡一直装傻<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/53759eaf3ffd63fa751586014346a605_b.png" data-rawwidth="436" data-rawheight="275" class="origin_image zh-lightbox-thumb" width="436" data-original="https://pic2.zhimg.com/53759eaf3ffd63fa751586014346a605_r.png"&amp;amp;gt;<img src="https://pic2.zhimg.com/53759eaf3ffd63fa751586014346a605_b.png" class="origin_image zh-lightbox-thumb lazy" width="436" />&amp;amp;lt;img src="https://pic3.zhimg.com/7ee4314b30dcbc5638316b50580bfbe6_b.png" data-rawwidth="439" data-rawheight="466" class="origin_image zh-lightbox-thumb" width="439" data-original="https://pic3.zhimg.com/7ee4314b30dcbc5638316b50580bfbe6_r.png"&amp;amp;gt;——————————————————————————————————————————<img src="https://pic3.zhimg.com/7ee4314b30dcbc5638316b50580bfbe6_b.png" class="origin_image zh-lightbox-thumb lazy" width="439" />——————————————————————————————————————————<br />\r\n<b>第二个谎言：流泪原因</b><br />\r\n九年的等待，两年的跟踪生涯终于结束，想到终于能和自少最憧憬的人说话忍不住泪流满面<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/ae0af99c61f7e162a3ac9e018e4e8830_b.png" data-rawwidth="457" data-rawheight="202" class="origin_image zh-lightbox-thumb" width="457" data-original="https://pic1.zhimg.com/ae0af99c61f7e162a3ac9e018e4e8830_r.png"&amp;amp;gt;<img src="https://pic1.zhimg.com/ae0af99c61f7e162a3ac9e018e4e8830_b.png" class="origin_image zh-lightbox-thumb lazy" width="457" />&amp;amp;lt;img src="https://pic3.zhimg.com/c3a799c3d7a7f1735507c649ead55726_b.png" data-rawwidth="438" data-rawheight="436" class="origin_image zh-lightbox-thumb" width="438" data-original="https://pic3.zhimg.com/c3a799c3d7a7f1735507c649ead55726_r.png"&amp;amp;gt;结局：半成功<img src="https://pic3.zhimg.com/c3a799c3d7a7f1735507c649ead55726_b.png" class="origin_image zh-lightbox-thumb lazy" width="438" />结局：半成功<br />\r\n由于美如画，公生忍不住拿起手机狂拍，无意拍了走光照，令宫园原形毕露，拳打脚踢给公生留下极差印象<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/b7ea9cada742e4fcfade0fc9f522e3cc_b.png" data-rawwidth="443" data-rawheight="453" class="origin_image zh-lightbox-thumb" width="443" data-original="https://pic1.zhimg.com/b7ea9cada742e4fcfade0fc9f522e3cc_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic1.zhimg.com/b7ea9cada742e4fcfade0fc9f522e3cc_b.png" class="origin_image zh-lightbox-thumb lazy" width="443" />———————————————————————————————————————————<br />\r\n<b>第三个谎言：强装笑容和镇定</b><br />\r\n宫园接近公生是为了实现5岁立下的愿望，和公生合奏首先必须让公生承认自己的实力，否则过去的一切努力都付诸东流<br />\r\n有马公生是5岁出道就能弹出向日葵，到十一岁前制霸全国人称人形<a href="https://link.zhihu.com/?target=http%3A/jump.bdimg.com/safecheck/Index%3Furl%3DrN3wPs8te/pL4AOY0zAwhz3wi8AXlR5gsMEbyYdIw62e189kmoSaIhBFoxzAa2FFImOuUl9obIdesUqvhcRz%2BhXSuwz9b96EragWmZ1jer1XQuxoOXhQsTEI3MdPltgpu%2BapwNVoH9geE%2BlT3vkD%2BdQgUInpETieq%2BzRSN%2BoGTjNdtRceiM/zp0aS/Pi9MWZKXR6kMppLHJ2Pah4egTNMg%3D%3D" class=" wrap external" target="_blank">节拍器<i class="icon-external"></i></a>的钢琴天才，想得到其承认难度可想而知，武士和村井的实力全国最强公生连看都不看<br />\r\n那一刻也是宫园计划最重要的一刻。<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/5d4f65a274e1a76cc3b67a7434f0349f_b.png" data-rawwidth="454" data-rawheight="346" class="origin_image zh-lightbox-thumb" width="454" data-original="https://pic4.zhimg.com/5d4f65a274e1a76cc3b67a7434f0349f_r.png"&amp;amp;gt;<img src="https://pic4.zhimg.com/5d4f65a274e1a76cc3b67a7434f0349f_b.png" class="origin_image zh-lightbox-thumb lazy" width="454" />&amp;amp;lt;img src="https://pic2.zhimg.com/733c48b3f0e65f4366d58ac86c7e3a3d_b.png" data-rawwidth="445" data-rawheight="305" class="origin_image zh-lightbox-thumb" width="445" data-original="https://pic2.zhimg.com/733c48b3f0e65f4366d58ac86c7e3a3d_r.png"&amp;amp;gt;小凪没必要隐瞒，所以当时她既紧张又惊慌，宫园和她那时的心情是一样的<img src="https://pic2.zhimg.com/733c48b3f0e65f4366d58ac86c7e3a3d_b.png" class="origin_image zh-lightbox-thumb lazy" width="445" />小凪没必要隐瞒，所以当时她既紧张又惊慌，宫园和她那时的心情是一样的<br />\r\n后公生知道宫园当时的表情和笑容都是强装的<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/92d5cffc059cc9b7effa292955e673bf_b.png" data-rawwidth="438" data-rawheight="616" class="origin_image zh-lightbox-thumb" width="438" data-original="https://pic4.zhimg.com/92d5cffc059cc9b7effa292955e673bf_r.png"&amp;amp;gt;结局：成功<img src="https://pic4.zhimg.com/92d5cffc059cc9b7effa292955e673bf_b.png" class="origin_image zh-lightbox-thumb lazy" width="438" />结局：成功<br />\r\n公生不擅长说谎，所以只能从另一角度解读宫园的演奏，也等于是变相承认了宫园的实力<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/aa784a358d4ce6a3d5e91832a1ccd674_b.png" data-rawwidth="434" data-rawheight="620" class="origin_image zh-lightbox-thumb" width="434" data-original="https://pic1.zhimg.com/aa784a358d4ce6a3d5e91832a1ccd674_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic1.zhimg.com/aa784a358d4ce6a3d5e91832a1ccd674_b.png" class="origin_image zh-lightbox-thumb lazy" width="434" />———————————————————————————————————————————<br />\r\n<b>第四个谎言：友人A</b><br />\r\n宫园一下课就去公生的必经之地等候，公生出现时高兴的脸蛋通红<br />\r\n不过为了不让公生误会，她只能叫“友人A”<br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/6ac58e0e835d5f6975baef19f7043e82_b.png" data-rawwidth="458" data-rawheight="658" class="origin_image zh-lightbox-thumb" width="458" data-original="https://pic3.zhimg.com/6ac58e0e835d5f6975baef19f7043e82_r.png"&amp;amp;gt;<img src="https://pic3.zhimg.com/6ac58e0e835d5f6975baef19f7043e82_b.png" class="origin_image zh-lightbox-thumb lazy" width="458" />&amp;amp;lt;img src="https://pic1.zhimg.com/d458893eabaaefb3512c67e590101ef8_b.png" data-rawwidth="374" data-rawheight="417" class="content_image" width="374"&amp;amp;gt;友人A从头至尾都是个谎言，宫园一直想叫出自己心中隐藏了九年的名字，但她明白，有些事是她不应该做的。<img src="https://pic1.zhimg.com/d458893eabaaefb3512c67e590101ef8_b.png" class="content_image lazy" width="374" />友人A从头至尾都是个谎言，宫园一直想叫出自己心中隐藏了九年的名字，但她明白，有些事是她不应该做的。<br />\r\n因为她只能是个路人<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/c28858083fb7e583c15c445520d0cb95_b.png" data-rawwidth="455" data-rawheight="443" class="origin_image zh-lightbox-thumb" width="455" data-original="https://pic2.zhimg.com/c28858083fb7e583c15c445520d0cb95_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic2.zhimg.com/c28858083fb7e583c15c445520d0cb95_b.png" class="origin_image zh-lightbox-thumb lazy" width="455" />———————————————————————————————————————————<br />\r\n<b>第五个谎言：掩饰身份，强行吹牛</b><br />\r\n入中学后，为了和公生搭话，宫园观察了公生两年，但最后都因为不想伤害小椿不想破坏公生身边的关系而进不了那个圈子<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/c97fd02e0e21e971b8c96b6e0071dd89_b.png" data-rawwidth="455" data-rawheight="690" class="origin_image zh-lightbox-thumb" width="455" data-original="https://pic2.zhimg.com/c97fd02e0e21e971b8c96b6e0071dd89_r.png"&amp;amp;gt;公生可能对小椿班上那个老是偷看自己的眼镜女孩有印象<img src="https://pic2.zhimg.com/c97fd02e0e21e971b8c96b6e0071dd89_b.png" class="origin_image zh-lightbox-thumb lazy" width="455" />公生可能对小椿班上那个老是偷看自己的眼镜女孩有印象<br />\r\n见公生好像若有所悟的样子，怕公生认出真面目就立即转移话题成功吸引了公生的注意力<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/0767727e7fb467a4eb42a3e1a8170ce0_b.png" data-rawwidth="455" data-rawheight="631" class="origin_image zh-lightbox-thumb" width="455" data-original="https://pic1.zhimg.com/0767727e7fb467a4eb42a3e1a8170ce0_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic1.zhimg.com/0767727e7fb467a4eb42a3e1a8170ce0_b.png" class="origin_image zh-lightbox-thumb lazy" width="455" />———————————————————————————————————————————<br />\r\n<b>第六个谎言：埋伏渡</b><br />\r\n只要是那个学校的学生应该都知道足球王子渡亮太的课后业余生活有多充实和丰富，单公生知道的女朋友就有三个，更不用说那些刻在课桌上随处可见的小雨伞了<br />\r\n埋伏渡的后果就是修罗场，也只有恋爱白痴单纯的公生会毫不怀疑<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/7501e8d7482656c98bde0bce8849859c_b.png" data-rawwidth="445" data-rawheight="355" class="origin_image zh-lightbox-thumb" width="445" data-original="https://pic1.zhimg.com/7501e8d7482656c98bde0bce8849859c_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic1.zhimg.com/7501e8d7482656c98bde0bce8849859c_b.png" class="origin_image zh-lightbox-thumb lazy" width="445" />———————————————————————————————————————————<br />\r\n<b>第七个谎言：窥探足球部</b><br />\r\n公生不擅说谎，看了公生的表情听到公生的话语宫园就知道重视友情的公生上套了，<br />\r\n为了弥补最好朋友的过失，公生必会做出表示<br />\r\n这样就找到了机会让公生陪自己<br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/9dd9dd73e2eee909cd2b87c2e6697652_b.png" data-rawwidth="449" data-rawheight="612" class="origin_image zh-lightbox-thumb" width="449" data-original="https://pic3.zhimg.com/9dd9dd73e2eee909cd2b87c2e6697652_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic3.zhimg.com/9dd9dd73e2eee909cd2b87c2e6697652_b.png" class="origin_image zh-lightbox-thumb lazy" width="449" />———————————————————————————————————————————<br />\r\n<b>第八个谎言：<a href="https://link.zhihu.com/?target=http%3A/jump.bdimg.com/safecheck/Index%3Furl%3DrN3wPs8te/pL4AOY0zAwhz3wi8AXlR5gsMEbyYdIw62IaMR1ZpsROrb/r6o65GtrImOuUl9obIdesUqvhcRz%2BhXSuwz9b96EragWmZ1jer1XQuxoOXhQsTEI3MdPltgpu%2BapwNVoH9geE%2BlT3vkD%2BdQgUInpETieq%2BzRSN%2BoGTjNdtRceiM/zp0aS/Pi9MWZKXR6kMppLHJ2Pah4egTNMg%3D%3D" class=" wrap external" target="_blank">华夫饼<i class="icon-external"></i></a>等点心</b><br />\r\n1、宫园家开蛋糕店，宫园什么点心没吃过<br />\r\n2、宫园是吃货，宫园什么点心没吃过<br />\r\n3、宫园家人很疼她，宫园什么点心没吃过<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/9a4950447026e7200e2062bc2f64a9d0_b.png" data-rawwidth="450" data-rawheight="306" class="origin_image zh-lightbox-thumb" width="450" data-original="https://pic1.zhimg.com/9a4950447026e7200e2062bc2f64a9d0_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic1.zhimg.com/9a4950447026e7200e2062bc2f64a9d0_b.png" class="origin_image zh-lightbox-thumb lazy" width="450" />———————————————————————————————————————————<br />\r\n<b>第九个谎言：钢琴</b><br />\r\n这家咖啡店最大的特色就是钢琴<br />\r\n店家不懂爱护钢琴，所以直接用鲜花来装饰，并任由店里的小孩子弹<br />\r\n这个时间过来，店里肯定会有接完孩子来这里歇息的主妇，孩子无聊肯定会弹钢琴，而温柔的公生对小孩子是最没辙的<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/1ff91244e191665f3defba1ff29562a7_b.png" data-rawwidth="469" data-rawheight="617" class="origin_image zh-lightbox-thumb" width="469" data-original="https://pic4.zhimg.com/1ff91244e191665f3defba1ff29562a7_r.png"&amp;amp;gt;<img src="https://pic4.zhimg.com/1ff91244e191665f3defba1ff29562a7_b.png" class="origin_image zh-lightbox-thumb lazy" width="469" />&amp;amp;lt;img src="https://pic1.zhimg.com/d4364b94ae69f266d0e8da7fd8be63e4_b.png" data-rawwidth="429" data-rawheight="401" class="origin_image zh-lightbox-thumb" width="429" data-original="https://pic1.zhimg.com/d4364b94ae69f266d0e8da7fd8be63e4_r.png"&amp;amp;gt;结局：成功<img src="https://pic1.zhimg.com/d4364b94ae69f266d0e8da7fd8be63e4_b.png" class="origin_image zh-lightbox-thumb lazy" width="429" />结局：成功<br />\r\n公生首次弹奏联结两人命运的小星星之歌<br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/76111317dacce7d65505505a09b1dc8a_b.png" data-rawwidth="355" data-rawheight="413" class="content_image" width="355"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic3.zhimg.com/76111317dacce7d65505505a09b1dc8a_b.png" class="content_image lazy" width="355" />———————————————————————————————————————————<br />\r\n<b>第十个谎言：我们就是要这样活下去的人</b><br />\r\n在公生气馁时，宫园愤怒的指责和豪言<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/bd96151ea2951003049c49f9ccefc90f_b.png" data-rawwidth="401" data-rawheight="264" class="content_image" width="401"&amp;amp;gt;结局：失败<img src="https://pic4.zhimg.com/bd96151ea2951003049c49f9ccefc90f_b.png" class="content_image lazy" width="401" />结局：失败<br />\r\n公生依旧假笑逃避，否定自我<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/775b72530817942ee156bd2cd41ff8d0_b.png" data-rawwidth="458" data-rawheight="492" class="origin_image zh-lightbox-thumb" width="458" data-original="https://pic1.zhimg.com/775b72530817942ee156bd2cd41ff8d0_r.png"&amp;amp;gt;真相：<img src="https://pic1.zhimg.com/775b72530817942ee156bd2cd41ff8d0_b.png" class="origin_image zh-lightbox-thumb lazy" width="458" />真相：<br />\r\n宫园没有她自己说的那样坚强和无畏，她本质上是个非常脆弱的女孩，<br />\r\n所有的坚强都只是表面上的伪装<br />\r\n所有的坚强都只是为了掩饰自身的脆弱<br />\r\n所有的坚强都只是为了让公生振作的假象<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/b6afc16370d97b542341cb18d3076e2b_b.png" data-rawwidth="435" data-rawheight="667" class="origin_image zh-lightbox-thumb" width="435" data-original="https://pic4.zhimg.com/b6afc16370d97b542341cb18d3076e2b_r.png"&amp;amp;gt;在知道自己今后再也无法拿起小提琴后，宫园当天晚上一直在掩面流泪,自那一天起，宫园一直想自杀，<img src="https://pic4.zhimg.com/b6afc16370d97b542341cb18d3076e2b_b.png" class="origin_image zh-lightbox-thumb lazy" width="435" />在知道自己今后再也无法拿起小提琴后，宫园当天晚上一直在掩面流泪,自那一天起，宫园一直想自杀，<br />\r\n因为自幼重病的宫园活着的最大愿望和动力就是和公生合奏，让所有的听众铭记自己的音乐<br />\r\n现在希望没有了<br />\r\n忍受痛苦活下去也已经没有任何的意义<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/b97258a41904e2b3f6c7f2e3f0422680_b.png" data-rawwidth="182" data-rawheight="294" class="content_image" width="182"&amp;amp;gt;本质上宫园和公生一样，都是敏感和脆弱，但宫园为了公生幸福能够一直忍受折磨，一直强颜欢笑，一直让公生处在幸福的谎言中直到生命的终结.......<img src="https://pic1.zhimg.com/b97258a41904e2b3f6c7f2e3f0422680_b.png" class="content_image lazy" width="182" />本质上宫园和公生一样，都是敏感和脆弱，但宫园为了公生幸福能够一直忍受折磨，一直强颜欢笑，一直让公生处在幸福的谎言中直到生命的终结.......<br />\r\n———————————————————————————————————————————<br />\r\n<b>第十一个谎言：还有我在</b><br />\r\n每当公生迷惘、痛苦、孤独和绝望的时候，宫园都会微笑地说出这句话<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/f749e736cfa53d35bcad9eddf83bf3d1_b.png" data-rawwidth="457" data-rawheight="648" class="origin_image zh-lightbox-thumb" width="457" data-original="https://pic2.zhimg.com/f749e736cfa53d35bcad9eddf83bf3d1_r.png"&amp;amp;gt;<img src="https://pic2.zhimg.com/f749e736cfa53d35bcad9eddf83bf3d1_b.png" class="origin_image zh-lightbox-thumb lazy" width="457" />&amp;amp;lt;img src="https://pic3.zhimg.com/9ec64ea004d7c3b5aa1ed736d95ae7f6_b.png" data-rawwidth="461" data-rawheight="603" class="origin_image zh-lightbox-thumb" width="461" data-original="https://pic3.zhimg.com/9ec64ea004d7c3b5aa1ed736d95ae7f6_r.png"&amp;amp;gt;<img src="https://pic3.zhimg.com/9ec64ea004d7c3b5aa1ed736d95ae7f6_b.png" class="origin_image zh-lightbox-thumb lazy" width="461" />&amp;amp;lt;img src="https://pic1.zhimg.com/4c39dce9eed8db6a632223e6a89430c0_b.png" data-rawwidth="451" data-rawheight="645" class="origin_image zh-lightbox-thumb" width="451" data-original="https://pic1.zhimg.com/4c39dce9eed8db6a632223e6a89430c0_r.png"&amp;amp;gt;<img src="https://pic1.zhimg.com/4c39dce9eed8db6a632223e6a89430c0_b.png" class="origin_image zh-lightbox-thumb lazy" width="451" />&amp;amp;lt;img src="https://pic2.zhimg.com/232605a19688d8a34e97fd02e1098689_b.png" data-rawwidth="344" data-rawheight="684" class="content_image" width="344"&amp;amp;gt;真相：宫园早已暗示这句话是谎言，下面这句话的隐意是“我会在你幸福的时候静静地离开”<img src="https://pic2.zhimg.com/232605a19688d8a34e97fd02e1098689_b.png" class="content_image lazy" width="344" />真相：宫园早已暗示这句话是谎言，下面这句话的隐意是“我会在你幸福的时候静静地离开”<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/58259d15dab61885833d23baa3d2ea94_b.png" data-rawwidth="385" data-rawheight="520" class="content_image" width="385"&amp;amp;gt;现实：<img src="https://pic1.zhimg.com/58259d15dab61885833d23baa3d2ea94_b.png" class="content_image lazy" width="385" />现实：<br />\r\n在努力让公生取得幸福的背后，是宫园在背后苦苦忍受着病痛的折磨而疯狂的挣扎<br />\r\n宫园的病情发作时，那种疼痛可谓撕心裂肺，生不如死<br />\r\n前期宫园的痛苦可以勉强用药物抑制，所以她每次出外都需要带一挎包的止痛药，由于怕被朋友知道一般都是偷偷去厕所服用的<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/106f80865da7bc8faaaa41e179bd7950_b.png" data-rawwidth="440" data-rawheight="652" class="origin_image zh-lightbox-thumb" width="440" data-original="https://pic1.zhimg.com/106f80865da7bc8faaaa41e179bd7950_r.png"&amp;amp;gt;秋季起宫园的病情开始失控，药物已经无法压制了<img src="https://pic1.zhimg.com/106f80865da7bc8faaaa41e179bd7950_b.png" class="origin_image zh-lightbox-thumb lazy" width="440" />秋季起宫园的病情开始失控，药物已经无法压制了<br />\r\n所以她不想让公生他们来医院看望自己，直接打电话跟他们说不用过来<br />\r\n可惜公生终于肯正视自己的感情时也终于发现了真相<br />\r\n如果不是碰巧，他们永远不会发现宫园的秘密，也不会知道乐观开朗的宫园每天都在承受着什么样的痛苦<br />\r\n（由于剧情和细节过虐，20话动画把它们全部删改减，接下来开始还原）<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/6257ffb2bdf3456b7cbd88f1106d725f_b.png" data-rawwidth="436" data-rawheight="312" class="origin_image zh-lightbox-thumb" width="436" data-original="https://pic4.zhimg.com/6257ffb2bdf3456b7cbd88f1106d725f_r.png"&amp;amp;gt;当时宫园痛得死死抓住床杆不放，右手直接扭曲成九十度，指关节死死地抓住床杆，由于太过用力，纤弱手指的骨头被冰冷的铁栏磨得嘎达作响，嘎达嘎达的碎骨声让人心惊胆战<img src="https://pic4.zhimg.com/6257ffb2bdf3456b7cbd88f1106d725f_b.png" class="origin_image zh-lightbox-thumb lazy" width="436" />当时宫园痛得死死抓住床杆不放，右手直接扭曲成九十度，指关节死死地抓住床杆，由于太过用力，纤弱手指的骨头被冰冷的铁栏磨得嘎达作响，嘎达嘎达的碎骨声让人心惊胆战<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/27a73681cfeb3f3e3b342a977286cfb7_b.png" data-rawwidth="435" data-rawheight="539" class="origin_image zh-lightbox-thumb" width="435" data-original="https://pic4.zhimg.com/27a73681cfeb3f3e3b342a977286cfb7_r.png"&amp;amp;gt;由探望时间和身上毛衣可知宫园是在做完康复训练后回到病房发作的<img src="https://pic4.zhimg.com/27a73681cfeb3f3e3b342a977286cfb7_b.png" class="origin_image zh-lightbox-thumb lazy" width="435" />由探望时间和身上毛衣可知宫园是在做完康复训练后回到病房发作的<br />\r\n为什么明明如此虚弱，宫园仍然要做康复训练<br />\r\n因为和公生的约定<br />\r\n因为和公生说好要再一次一起演奏<br />\r\n倒下时宫园身边并没有医生，而且她是有知觉的，从她激烈的反应知道她没有打麻醉针或吃止痛药之类的东西，纯粹是用自己的肉体去忍受那痛彻心扉的折磨<br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/176d8a7c2e758afce0bac7ace6d34b3e_b.png" data-rawwidth="444" data-rawheight="379" class="origin_image zh-lightbox-thumb" width="444" data-original="https://pic3.zhimg.com/176d8a7c2e758afce0bac7ace6d34b3e_r.png"&amp;amp;gt;更恐怖的是全程宫园都不肯吭声，众所周知喊叫可以缓解疼痛，楼主冲个澡有时都被烫的痛嚎不已，而宫园则是全程紧咬牙关一声不吭，可能是不肯投降，可能是不想认输<img src="https://pic3.zhimg.com/176d8a7c2e758afce0bac7ace6d34b3e_b.png" class="origin_image zh-lightbox-thumb lazy" width="444" />更恐怖的是全程宫园都不肯吭声，众所周知喊叫可以缓解疼痛，楼主冲个澡有时都被烫的痛嚎不已，而宫园则是全程紧咬牙关一声不吭，可能是不肯投降，可能是不想认输<br />\r\n她只是死死地抓住床杆，靠着扭断自己的双手拧断自己的手指来缓解那求生不得求死不能的折磨<br />\r\n即使这样，宫园仍然不肯失去意识，因为她向公生约定了自己要做好不遗余力挣扎的准备<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/ad7e125f26f1cc5dbcee23ed67ef0509_b.png" data-rawwidth="373" data-rawheight="598" class="content_image" width="373"&amp;amp;gt;所以可能在之前或之后每一次病情发作的时候，宫园都是咬碎牙齿拼命忍耐，直到最后活生生痛晕过去才得以解脱<img src="https://pic2.zhimg.com/ad7e125f26f1cc5dbcee23ed67ef0509_b.png" class="content_image lazy" width="373" />所以可能在之前或之后每一次病情发作的时候，宫园都是咬碎牙齿拼命忍耐，直到最后活生生痛晕过去才得以解脱<br />\r\n要不是公生他们过来，他们永远不会知道这个秘密，他们只会见到每天傍晚带着一脸笑容迎接他们的那个快乐明朗的女孩<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/b10a9e0f640f654e895e1752fe7d37c5_b.png" data-rawwidth="432" data-rawheight="690" class="origin_image zh-lightbox-thumb" width="432" data-original="https://pic2.zhimg.com/b10a9e0f640f654e895e1752fe7d37c5_r.png"&amp;amp;gt;为什么宫园想自杀<img src="https://pic2.zhimg.com/b10a9e0f640f654e895e1752fe7d37c5_b.png" class="origin_image zh-lightbox-thumb lazy" width="432" />为什么宫园想自杀<br />\r\n为什么宫园经常泪流满面<br />\r\n为什么宫园要奔跑着生活<br />\r\n为什么宫园要催逼着公生弹琴<br />\r\n答案是显而易见的<br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/3263d9820574e219725ecebef0965b82_b.png" data-rawwidth="439" data-rawheight="618" class="origin_image zh-lightbox-thumb" width="439" data-original="https://pic3.zhimg.com/3263d9820574e219725ecebef0965b82_r.png"&amp;amp;gt;作者很良心或者根本不敢放出宫园面部的特写，但宫园此时肯定是紧咬牙关，表情狰狞而又泪流满面，明明痛的死去活来还要强迫自己咬断牙关不吭声<img src="https://pic3.zhimg.com/3263d9820574e219725ecebef0965b82_b.png" class="origin_image zh-lightbox-thumb lazy" width="439" />作者很良心或者根本不敢放出宫园面部的特写，但宫园此时肯定是紧咬牙关，表情狰狞而又泪流满面，明明痛的死去活来还要强迫自己咬断牙关不吭声<br />\r\n为什么宫园要这么拼<br />\r\n为什么宫园要这么傻<br />\r\n为什么宫园要天天忍受这种痛苦都不肯自杀<br />\r\n因为她和公生做了约定，一定要战胜命运再一次合奏<br />\r\n但现实是残酷的，人定胜天之类的话本身就是违心的谎言<br />\r\n宫园直到最后都不肯失去意识，直到最后被疼痛折磨得晕死过去本身也反映了现实的无奈<br />\r\n地上被践踏的卡娜蕾也反映出命运的无情，宫园的努力和挣扎在老天面前就是个无聊的笑话，犹如路边被任意踩踏的垃圾<br />\r\n此时，公生才明白宫园右手的意义，<br />\r\n那只紧握琴弓的手<br />\r\n那只牵着自己的手<br />\r\n那只和紧贴掌心的手<br />\r\n那只骨裂无力摇摇欲坠的手<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/454caf95228f96e611a6298bf690bba5_b.png" data-rawwidth="429" data-rawheight="638" class="origin_image zh-lightbox-thumb" width="429" data-original="https://pic2.zhimg.com/454caf95228f96e611a6298bf690bba5_r.png"&amp;amp;gt;此时，公生才明白，宫园最好的归宿可能就是死亡<img src="https://pic2.zhimg.com/454caf95228f96e611a6298bf690bba5_b.png" class="origin_image zh-lightbox-thumb lazy" width="429" />此时，公生才明白，宫园最好的归宿可能就是死亡<br />\r\n因为对宫园来说，活着就意味着痛苦，活着就意味着无奈，活着就意味着绝望....<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/c0d44fc5a9c49f08899eeddbe6e5bd79_b.png" data-rawwidth="430" data-rawheight="640" class="origin_image zh-lightbox-thumb" width="430" data-original="https://pic2.zhimg.com/c0d44fc5a9c49f08899eeddbe6e5bd79_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic2.zhimg.com/c0d44fc5a9c49f08899eeddbe6e5bd79_b.png" class="origin_image zh-lightbox-thumb lazy" width="430" />———————————————————————————————————————————<br />\r\n<b>第十二个谎言：只是慎重起见的入院检查</b><br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/c729504513a09e3ad87183bfdf00ea43_b.png" data-rawwidth="442" data-rawheight="182" class="origin_image zh-lightbox-thumb" width="442" data-original="https://pic4.zhimg.com/c729504513a09e3ad87183bfdf00ea43_r.png"&amp;amp;gt;两个从来没进过医院的笨蛋不加思索就相信了<img src="https://pic4.zhimg.com/c729504513a09e3ad87183bfdf00ea43_b.png" class="origin_image zh-lightbox-thumb lazy" width="442" />两个从来没进过医院的笨蛋不加思索就相信了<br />\r\n这个不奇怪，渡是个单细胞海绵体，小椿打个喷嚏拿个B都会世界震惊，这两笨蛋不是因为探望宫园相信到了八十岁都不用去医院<br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/9f1bf3d4695dc5e87d6d06d709653be6_b.png" data-rawwidth="188" data-rawheight="301" class="content_image" width="188"&amp;amp;gt;<img src="https://pic3.zhimg.com/9f1bf3d4695dc5e87d6d06d709653be6_b.png" class="content_image lazy" width="188" />&amp;amp;lt;img src="https://pic2.zhimg.com/510aefadf18c812c1116bc49556028d9_b.png" data-rawwidth="438" data-rawheight="260" class="origin_image zh-lightbox-thumb" width="438" data-original="https://pic2.zhimg.com/510aefadf18c812c1116bc49556028d9_r.png"&amp;amp;gt;只有公生有疑惑，因为检查这个借口当年母亲用了无数次<img src="https://pic2.zhimg.com/510aefadf18c812c1116bc49556028d9_b.png" class="origin_image zh-lightbox-thumb lazy" width="438" />只有公生有疑惑，因为检查这个借口当年母亲用了无数次<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/2959b657a3f1b83b934b55499b244234_b.png" data-rawwidth="436" data-rawheight="251" class="origin_image zh-lightbox-thumb" width="436" data-original="https://pic1.zhimg.com/2959b657a3f1b83b934b55499b244234_r.png"&amp;amp;gt;宫园知道公生母亲的事，见公生追问，马上一口黑锅扔过去，被转移仇恨的公生再遭践踏<img src="https://pic1.zhimg.com/2959b657a3f1b83b934b55499b244234_b.png" class="origin_image zh-lightbox-thumb lazy" width="436" />宫园知道公生母亲的事，见公生追问，马上一口黑锅扔过去，被转移仇恨的公生再遭践踏<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/21e2b87039168ee3cbe01c2ce14799c8_b.png" data-rawwidth="439" data-rawheight="295" class="origin_image zh-lightbox-thumb" width="439" data-original="https://pic1.zhimg.com/21e2b87039168ee3cbe01c2ce14799c8_r.png"&amp;amp;gt;结局：成功<img src="https://pic1.zhimg.com/21e2b87039168ee3cbe01c2ce14799c8_b.png" class="origin_image zh-lightbox-thumb lazy" width="439" />结局：成功<br />\r\n完美地转移了所有人的注意，并故意在大伙面前把点心吃的满嘴都是表示胃好身体好<br />\r\n一个把止痛药当饭吃、身体消瘦的病人会有胃口？这种吃货打着灯笼都找不到<br />\r\n可以参考在人类面前金木和董香是怎样吃饭的！<br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/f30dba2f1d375dddde811436d8467d0e_b.png" data-rawwidth="432" data-rawheight="312" class="origin_image zh-lightbox-thumb" width="432" data-original="https://pic3.zhimg.com/f30dba2f1d375dddde811436d8467d0e_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic3.zhimg.com/f30dba2f1d375dddde811436d8467d0e_b.png" class="origin_image zh-lightbox-thumb lazy" width="432" />———————————————————————————————————————————<br />\r\n<b>第十三个谎言：小卖部门口玩手机</b><br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/8fdde3ccfb94fa9bcc8f19bf73d63801_b.png" data-rawwidth="344" data-rawheight="275" class="content_image" width="344"&amp;amp;gt;死亡开始逼近，时间已经不够，宫园也采取了更为积极主动的方式<img src="https://pic2.zhimg.com/8fdde3ccfb94fa9bcc8f19bf73d63801_b.png" class="content_image lazy" width="344" />死亡开始逼近，时间已经不够，宫园也采取了更为积极主动的方式<br />\r\n公生是个一丝不苟的人，做事一板一眼很有条理性，任何时候早到五分钟是雷打不动的铁则，就连上学放学，买面包的时间也是固定的<br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/502424d7eccee9078107532e8d96f18e_b.png" data-rawwidth="431" data-rawheight="463" class="origin_image zh-lightbox-thumb" width="431" data-original="https://pic3.zhimg.com/502424d7eccee9078107532e8d96f18e_r.png"&amp;amp;gt;<img src="https://pic3.zhimg.com/502424d7eccee9078107532e8d96f18e_b.png" class="origin_image zh-lightbox-thumb lazy" width="431" />&amp;amp;lt;img src="https://pic1.zhimg.com/6bf880a595694adfb7f074a2237bb574_b.png" data-rawwidth="438" data-rawheight="191" class="origin_image zh-lightbox-thumb" width="438" data-original="https://pic1.zhimg.com/6bf880a595694adfb7f074a2237bb574_r.png"&amp;amp;gt;<img src="https://pic1.zhimg.com/6bf880a595694adfb7f074a2237bb574_b.png" class="origin_image zh-lightbox-thumb lazy" width="438" />&amp;amp;lt;img src="https://pic1.zhimg.com/35728a2f820008047e2833928ce175c4_b.png" data-rawwidth="457" data-rawheight="299" class="origin_image zh-lightbox-thumb" width="457" data-original="https://pic1.zhimg.com/35728a2f820008047e2833928ce175c4_r.png"&amp;amp;gt;<img src="https://pic1.zhimg.com/35728a2f820008047e2833928ce175c4_b.png" class="origin_image zh-lightbox-thumb lazy" width="457" /><br />\r\n观察了公生两年的宫园早洞察了一切<br />\r\n在公生拿着鸡蛋三明治幸福地走出来时<br />\r\n宫园早已拿着手机对好时间在一旁默默地等待<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/29e532c1b433af4f842708cc1830d149_b.png" data-rawwidth="341" data-rawheight="224" class="content_image" width="341"&amp;amp;gt;<img src="https://pic2.zhimg.com/29e532c1b433af4f842708cc1830d149_b.png" class="content_image lazy" width="341" />&amp;amp;lt;img src="https://pic2.zhimg.com/65000ec989ae0590215349683e21a3fd_b.png" data-rawwidth="386" data-rawheight="560" class="content_image" width="386"&amp;amp;gt;结局：失败<img src="https://pic2.zhimg.com/65000ec989ae0590215349683e21a3fd_b.png" class="content_image lazy" width="386" />结局：失败<br />\r\n一直眼镜妹的宫园低估了自己的杀伤力，就如金子在哪里都会发光，宫园那种美女在哪里当然都是光彩照人，吸引群狼的<br />\r\n就在公生不自觉地躲起来整理情绪和话术准备来个偶遇时，色狼老远就被吸引了过来，面对美女阿渡自然是驾轻就熟<br />\r\n自卑的公生注定孤独一生，宫园当时也被气得够呛<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/bb841ca3f97e6fcb8cd908a0f8114fdf_b.png" data-rawwidth="457" data-rawheight="227" class="origin_image zh-lightbox-thumb" width="457" data-original="https://pic4.zhimg.com/bb841ca3f97e6fcb8cd908a0f8114fdf_r.png"&amp;amp;gt;<img src="https://pic4.zhimg.com/bb841ca3f97e6fcb8cd908a0f8114fdf_b.png" class="origin_image zh-lightbox-thumb lazy" width="457" />&amp;amp;lt;img src="https://pic2.zhimg.com/310d1ff4840d12d2f82ac5eb319bcd09_b.png" data-rawwidth="455" data-rawheight="142" class="origin_image zh-lightbox-thumb" width="455" data-original="https://pic2.zhimg.com/310d1ff4840d12d2f82ac5eb319bcd09_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic2.zhimg.com/310d1ff4840d12d2f82ac5eb319bcd09_b.png" class="origin_image zh-lightbox-thumb lazy" width="455" />———————————————————————————————————————————<br />\r\n<b>第十四个谎言：跳飞机</b><br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/b48c5f74b72160482b49d197c47f863e_b.png" data-rawwidth="392" data-rawheight="593" class="content_image" width="392"&amp;amp;gt;小卖部计划失败，立即实施试胆桥计划<img src="https://pic3.zhimg.com/b48c5f74b72160482b49d197c47f863e_b.png" class="content_image lazy" width="392" />小卖部计划失败，立即实施试胆桥计划<br />\r\n和公生一样，宫园对小孩也是没辙，所以立即被出卖<br />\r\n这反映了宫园本质的单纯，她并不擅长说谎，也不喜欢说谎<br />\r\n可能只有公生这么迟钝的人才会相信那些拙劣的谎言，那些不似谎言的谎言<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/6b0ed83db58e37b4ebc479391db52fac_b.png" data-rawwidth="376" data-rawheight="598" class="content_image" width="376"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic1.zhimg.com/6b0ed83db58e37b4ebc479391db52fac_b.png" class="content_image lazy" width="376" />———————————————————————————————————————————<br />\r\n<b>第十五个谎言：假哭</b><br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/60d98bfe75b84c2421341a93c96870e8_b.png" data-rawwidth="435" data-rawheight="449" class="origin_image zh-lightbox-thumb" width="435" data-original="https://pic1.zhimg.com/60d98bfe75b84c2421341a93c96870e8_r.png"&amp;amp;gt;<img src="https://pic1.zhimg.com/60d98bfe75b84c2421341a93c96870e8_b.png" class="origin_image zh-lightbox-thumb lazy" width="435" />&amp;amp;lt;img src="https://pic4.zhimg.com/c8fa6f5b459592049952637c7713d907_b.png" data-rawwidth="236" data-rawheight="637" class="content_image" width="236"&amp;amp;gt;假哭是撒娇的最高层次了，如果一个女生向你撒娇到假哭的程度,那基本说明她对你有意思没跑了,<img src="https://pic4.zhimg.com/c8fa6f5b459592049952637c7713d907_b.png" class="content_image lazy" width="236" />\r\n	<p>\r\n		假哭是撒娇的最高层次了，如果一个女生向你撒娇到假哭的程度,那基本说明她对你有意思没跑了,<br />\r\n一般女生不会向一个她不爱的人撒娇的,更不用说到假哭的程度\r\n	</p>\r\n<br />\r\n有些人注定孤独一生     （作为搬运工的我为什么到了这里会背脊一凉。。。）&amp;amp;lt;img src="https://pic4.zhimg.com/bb841ca3f97e6fcb8cd908a0f8114fdf_b.png" data-rawwidth="457" data-rawheight="227" class="origin_image zh-lightbox-thumb" width="457" data-original="https://pic4.zhimg.com/bb841ca3f97e6fcb8cd908a0f8114fdf_r.png"&amp;amp;gt;<img src="https://pic4.zhimg.com/bb841ca3f97e6fcb8cd908a0f8114fdf_b.png" class="origin_image zh-lightbox-thumb lazy" width="457" />&amp;amp;lt;img src="https://pic2.zhimg.com/310d1ff4840d12d2f82ac5eb319bcd09_b.png" data-rawwidth="455" data-rawheight="142" class="origin_image zh-lightbox-thumb" width="455" data-original="https://pic2.zhimg.com/310d1ff4840d12d2f82ac5eb319bcd09_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic2.zhimg.com/310d1ff4840d12d2f82ac5eb319bcd09_b.png" class="origin_image zh-lightbox-thumb lazy" width="455" />———————————————————————————————————————————<br />\r\n<b>第十六个谎言：看渡的社团活动</b><br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/327e25486c428fc8b4c3415ae25b696d_b.png" data-rawwidth="426" data-rawheight="366" class="origin_image zh-lightbox-thumb" width="426" data-original="https://pic2.zhimg.com/327e25486c428fc8b4c3415ae25b696d_r.png"&amp;amp;gt;虽然本人对钢琴曲不太了解但也看得出，宫园在教导公生前做了大量的功课，甚至曲子都是宫园研究后选的<img src="https://pic2.zhimg.com/327e25486c428fc8b4c3415ae25b696d_b.png" class="origin_image zh-lightbox-thumb lazy" width="426" />虽然本人对钢琴曲不太了解但也看得出，宫园在教导公生前做了大量的功课，甚至曲子都是宫园研究后选的<br />\r\n她给了听不见琴声的公生两个提示<br />\r\n1、为了什么去弹钢琴<br />\r\n2、想如何弹钢琴<br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/324287bbbda605f9a036d7b56d7fc1d5_b.png" data-rawwidth="400" data-rawheight="385" class="content_image" width="400"&amp;amp;gt;<img src="https://pic2.zhimg.com/324287bbbda605f9a036d7b56d7fc1d5_b.png" class="content_image lazy" width="400" />&amp;amp;lt;img src="https://pic3.zhimg.com/d3355324cf0c0ca2a1c617173dbac2f2_b.png" data-rawwidth="437" data-rawheight="318" class="origin_image zh-lightbox-thumb" width="437" data-original="https://pic3.zhimg.com/d3355324cf0c0ca2a1c617173dbac2f2_r.png"&amp;amp;gt;见到公生炙热的眼神好似若有所悟<img src="https://pic3.zhimg.com/d3355324cf0c0ca2a1c617173dbac2f2_b.png" class="origin_image zh-lightbox-thumb lazy" width="437" />见到公生炙热的眼神好似若有所悟<br />\r\n两人四目相对时，为怕旁边观众误会也为了让公生有更多思考时间，立马逃走借口去看阿渡表现<br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/09dc703fb8e956010c3bc6e245eb3632_b.png" data-rawwidth="319" data-rawheight="465" class="content_image" width="319"&amp;amp;gt;结局：在宫园的提点帮助下，公生重新找回了自我，回到了五岁时那个无拘无束的自己，弹出了崇尚阳光自由的向日葵<img src="https://pic3.zhimg.com/09dc703fb8e956010c3bc6e245eb3632_b.png" class="content_image lazy" width="319" />结局：在宫园的提点帮助下，公生重新找回了自我，回到了五岁时那个无拘无束的自己，弹出了崇尚阳光自由的向日葵<br />\r\n公生不但涅盘重生，更明白了自己的感情和继续弹琴的目的和理由<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/4947650007b53a71b3e265ee396b3864_b.png" data-rawwidth="580" data-rawheight="433" class="origin_image zh-lightbox-thumb" width="580" data-original="https://pic1.zhimg.com/4947650007b53a71b3e265ee396b3864_r.png"&amp;amp;gt;<img src="https://pic1.zhimg.com/4947650007b53a71b3e265ee396b3864_b.png" class="origin_image zh-lightbox-thumb lazy" width="580" />&amp;amp;lt;img src="https://pic3.zhimg.com/f45b51476bd1b0d59516efc29c66ff26_b.png" data-rawwidth="391" data-rawheight="600" class="content_image" width="391"&amp;amp;gt;<img src="https://pic3.zhimg.com/f45b51476bd1b0d59516efc29c66ff26_b.png" class="content_image lazy" width="391" />&amp;amp;lt;img src="https://pic4.zhimg.com/c6aee9a635c2e8d47f2094194d7069b3_b.png" data-rawwidth="482" data-rawheight="494" class="origin_image zh-lightbox-thumb" width="482" data-original="https://pic4.zhimg.com/c6aee9a635c2e8d47f2094194d7069b3_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic4.zhimg.com/c6aee9a635c2e8d47f2094194d7069b3_b.png" class="origin_image zh-lightbox-thumb lazy" width="482" />———————————————————————————————————————————<br />\r\n<b>第十七个谎言：弟弟<br />\r\n</b><br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/b6f76d504516abd4b24fa73bc7c4f3f6_b.png" data-rawwidth="437" data-rawheight="454" class="origin_image zh-lightbox-thumb" width="437" data-original="https://pic3.zhimg.com/b6f76d504516abd4b24fa73bc7c4f3f6_r.png"&amp;amp;gt;两年的观察宫园早已看透了一切，小椿和自己一样，最喜欢的人就是公生<img src="https://pic3.zhimg.com/b6f76d504516abd4b24fa73bc7c4f3f6_b.png" class="origin_image zh-lightbox-thumb lazy" width="437" />两年的观察宫园早已看透了一切，小椿和自己一样，最喜欢的人就是公生<br />\r\n自己的时间已经不多而且自己的愿望已经实现，接下来要让帮自己实现愿望的公生幸福，而那个能给公生幸福的人不是自己，而是小椿<br />\r\n所以宫园否定了自己对公生的感情，她知道公生喜欢自己，但直到死亡她都没有给过正面答复，更一直给小椿送上助攻希望撮合他们<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/7e1a3d5dc2d429ab23caad88a4db72cb_b.png" data-rawwidth="441" data-rawheight="620" class="origin_image zh-lightbox-thumb" width="441" data-original="https://pic4.zhimg.com/7e1a3d5dc2d429ab23caad88a4db72cb_r.png"&amp;amp;gt;<img src="https://pic4.zhimg.com/7e1a3d5dc2d429ab23caad88a4db72cb_b.png" class="origin_image zh-lightbox-thumb lazy" width="441" />&amp;amp;lt;img src="https://pic4.zhimg.com/80ffd25b2d34df413615343623c1b1c3_b.png" data-rawwidth="241" data-rawheight="155" class="content_image" width="241"&amp;amp;gt;<img src="https://pic4.zhimg.com/80ffd25b2d34df413615343623c1b1c3_b.png" class="content_image lazy" width="241" />&amp;amp;lt;img src="https://pic1.zhimg.com/a98620e2a4a990ec5839d4c02c8b6a1c_b.png" data-rawwidth="264" data-rawheight="257" class="content_image" width="264"&amp;amp;gt;<img src="https://pic1.zhimg.com/a98620e2a4a990ec5839d4c02c8b6a1c_b.png" class="content_image lazy" width="264" />&amp;amp;lt;img src="https://pic2.zhimg.com/a27fb4109ce197a4596d49d878731361_b.png" data-rawwidth="439" data-rawheight="166" class="origin_image zh-lightbox-thumb" width="439" data-original="https://pic2.zhimg.com/a27fb4109ce197a4596d49d878731361_r.png"&amp;amp;gt;<img src="https://pic2.zhimg.com/a27fb4109ce197a4596d49d878731361_b.png" class="origin_image zh-lightbox-thumb lazy" width="439" />&amp;amp;lt;img src="https://pic2.zhimg.com/4e956370342a2b708fd7952521a957f1_b.png" data-rawwidth="444" data-rawheight="446" class="origin_image zh-lightbox-thumb" width="444" data-original="https://pic2.zhimg.com/4e956370342a2b708fd7952521a957f1_r.png"&amp;amp;gt;结局：失败<img src="https://pic2.zhimg.com/4e956370342a2b708fd7952521a957f1_b.png" class="origin_image zh-lightbox-thumb lazy" width="444" />结局：失败<br />\r\n宫园带公生看垒球比赛本意是让公生替小椿加油，可惜被小椿误会成示威<br />\r\n宫园的解释里小椿从“我们”这个词和宫园对公生的眼神知道宫园在说谎<br />\r\n两个同样爱公生的人都觉得自己对公生的用处比不上对方，为了公生幸福，小椿决定退出<br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/c7ab3e95e545f7dfdecf000816712330_b.png" data-rawwidth="441" data-rawheight="682" class="origin_image zh-lightbox-thumb" width="441" data-original="https://pic1.zhimg.com/c7ab3e95e545f7dfdecf000816712330_r.png"&amp;amp;gt;<img src="https://pic1.zhimg.com/c7ab3e95e545f7dfdecf000816712330_b.png" class="origin_image zh-lightbox-thumb lazy" width="441" />&amp;amp;lt;img src="https://pic1.zhimg.com/68276214f70266b880077fd50362ac30_b.png" data-rawwidth="443" data-rawheight="338" class="origin_image zh-lightbox-thumb" width="443" data-original="https://pic1.zhimg.com/68276214f70266b880077fd50362ac30_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic1.zhimg.com/68276214f70266b880077fd50362ac30_b.png" class="origin_image zh-lightbox-thumb lazy" width="443" />———————————————————————————————————————————<br />\r\n<b>第十八个谎言：喜欢</b><br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/5541f3a64c493c598ca493861b06181e_b.png" data-rawwidth="439" data-rawheight="432" class="origin_image zh-lightbox-thumb" width="439" data-original="https://pic3.zhimg.com/5541f3a64c493c598ca493861b06181e_r.png"&amp;amp;gt;<img src="https://pic3.zhimg.com/5541f3a64c493c598ca493861b06181e_b.png" class="origin_image zh-lightbox-thumb lazy" width="439" />&amp;amp;lt;img src="https://pic3.zhimg.com/3a4ba268e13f37047c4b1000e6518d6a_b.png" data-rawwidth="435" data-rawheight="626" class="origin_image zh-lightbox-thumb" width="435" data-original="https://pic3.zhimg.com/3a4ba268e13f37047c4b1000e6518d6a_r.png"&amp;amp;gt;回答的时候刻意把公生和其他亲人朋友等同<img src="https://pic3.zhimg.com/3a4ba268e13f37047c4b1000e6518d6a_b.png" class="origin_image zh-lightbox-thumb lazy" width="435" />\r\n	<p>\r\n		回答的时候刻意把公生和其他亲人朋友等同<br />\r\n故意岔开话题，混淆概念<br />\r\n渡不是傻瓜，也没有公生那么迟钝，虽然吃惊但也觉得理所当然<br />\r\n渡没有拆穿宫园的谎言，但那一刻起他已经知道自己输了<br />\r\n公生是自己最好的朋友，渡决定维持现状<br />\r\n从这里也能明白渡为什么受欢迎的原因以及公生为什么把他当成最好的朋友\r\n	</p>\r\n<br />\r\n真相：宫园一直明白自己的角色定位，她不想伤害任何人，不想破坏公生和渡之间的友情，不想伤害渡也不想伤害小椿，所以直到去世一个月后公生心情平复时才把这些隐藏了九年的秘密全盘托出，让公生明白自己走得幸福、安详、毫无遗憾<br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/13617fd11cd7ef758f81790b84b37c93_b.png" data-rawwidth="402" data-rawheight="267" class="content_image" width="402"&amp;amp;gt;<img src="https://pic4.zhimg.com/13617fd11cd7ef758f81790b84b37c93_b.png" class="content_image lazy" width="402" />&amp;amp;lt;img src="https://pic2.zhimg.com/43aaf67db74a3d98af08677f2dd7e8b1_b.png" data-rawwidth="426" data-rawheight="605" class="origin_image zh-lightbox-thumb" width="426" data-original="https://pic2.zhimg.com/43aaf67db74a3d98af08677f2dd7e8b1_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic2.zhimg.com/43aaf67db74a3d98af08677f2dd7e8b1_b.png" class="origin_image zh-lightbox-thumb lazy" width="426" />———————————————————————————————————————————<br />\r\n<b>第十九个谎言：粉丝</b><br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/16cff3dfbcb02534184c8a5002726be1_b.png" data-rawwidth="315" data-rawheight="193" class="content_image" width="315"&amp;amp;gt;事实上宫园父母之前根本不认识也没接触过有马公生，最多就从女儿的描述或者照片之类知道公生的长相<img src="https://pic2.zhimg.com/16cff3dfbcb02534184c8a5002726be1_b.png" class="content_image lazy" width="315" />事实上宫园父母之前根本不认识也没接触过有马公生，最多就从女儿的描述或者照片之类知道公生的长相&amp;amp;lt;img src="https://pic1.zhimg.com/0efeca6fdec363fb7e1a713b0bda6738_b.png" data-rawwidth="435" data-rawheight="195" class="origin_image zh-lightbox-thumb" width="435" data-original="https://pic1.zhimg.com/0efeca6fdec363fb7e1a713b0bda6738_r.png"&amp;amp;gt;不过他们也很意外，五岁就能把自己可爱女儿勾引走的男孩原来是个礼数端正的好孩子，同时他们也担忧，这样的孩子是绝对承受不了残酷事实的<img src="https://pic1.zhimg.com/0efeca6fdec363fb7e1a713b0bda6738_b.png" class="origin_image zh-lightbox-thumb lazy" width="435" />不过他们也很意外，五岁就能把自己可爱女儿勾引走的男孩原来是个礼数端正的好孩子，同时他们也担忧，这样的孩子是绝对承受不了残酷事实的&amp;amp;lt;img src="https://pic3.zhimg.com/f46d09a15e96744ecc5b87c65b30603e_b.png" data-rawwidth="441" data-rawheight="211" class="origin_image zh-lightbox-thumb" width="441" data-original="https://pic3.zhimg.com/f46d09a15e96744ecc5b87c65b30603e_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic3.zhimg.com/f46d09a15e96744ecc5b87c65b30603e_b.png" class="origin_image zh-lightbox-thumb lazy" width="441" />———————————————————————————————————————————<br />\r\n<b>第二十个谎言：单纯的入院检查</b><br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/581e9b8c6285ef73518057137ed53e4e_b.png" data-rawwidth="385" data-rawheight="588" class="content_image" width="385"&amp;amp;gt;<img src="https://pic3.zhimg.com/581e9b8c6285ef73518057137ed53e4e_b.png" class="content_image lazy" width="385" />&amp;amp;lt;img src="https://pic4.zhimg.com/fec8d75667d6eb2b43177047053f1e63_b.png" data-rawwidth="443" data-rawheight="430" class="origin_image zh-lightbox-thumb" width="443" data-original="https://pic4.zhimg.com/fec8d75667d6eb2b43177047053f1e63_r.png"&amp;amp;gt;公生母亲有前科，所以这个谎言并不靠谱<img src="https://pic4.zhimg.com/fec8d75667d6eb2b43177047053f1e63_b.png" class="origin_image zh-lightbox-thumb lazy" width="443" />公生母亲有前科，所以这个谎言并不靠谱<br />\r\n宫园一直注意着公生的反应，见到公生好像有头绪再次转移仇恨<br />\r\n———————————————————————————————————————————<br />\r\n<b>第二十一个谎言：疲劳体质</b><br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/0694bc43482b0573c3fe28b42e9c6aa9_b.png" data-rawwidth="438" data-rawheight="296" class="origin_image zh-lightbox-thumb" width="438" data-original="https://pic2.zhimg.com/0694bc43482b0573c3fe28b42e9c6aa9_r.png"&amp;amp;gt;本人见识浅薄目前仍不清楚宫园得的是什么病，但应该属于那种容易疲劳而且四肢逐渐无力的病，病发时能把人活活痛晕甚至痛死的病，是一种无药可治的病，是一种亲人、医生、朋友见到都会绝望的病<img src="https://pic2.zhimg.com/0694bc43482b0573c3fe28b42e9c6aa9_b.png" class="origin_image zh-lightbox-thumb lazy" width="438" />本人见识浅薄目前仍不清楚宫园得的是什么病，但应该属于那种容易疲劳而且四肢逐渐无力的病，病发时能把人活活痛晕甚至痛死的病，是一种无药可治的病，是一种亲人、医生、朋友见到都会绝望的病&amp;amp;lt;img src="https://pic1.zhimg.com/ac23f41bf0bf3d0af8aed13be646e4a4_b.png" data-rawwidth="438" data-rawheight="382" class="origin_image zh-lightbox-thumb" width="438" data-original="https://pic1.zhimg.com/ac23f41bf0bf3d0af8aed13be646e4a4_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic1.zhimg.com/ac23f41bf0bf3d0af8aed13be646e4a4_b.png" class="origin_image zh-lightbox-thumb lazy" width="438" />———————————————————————————————————————————<br />\r\n<b>第二十二个谎言：第二学期会回学校</b><br />\r\n&amp;amp;lt;img src="https://pic2.zhimg.com/e319f589c2dc446fd54265f8d0431311_b.png" data-rawwidth="442" data-rawheight="365" class="origin_image zh-lightbox-thumb" width="442" data-original="https://pic2.zhimg.com/e319f589c2dc446fd54265f8d0431311_r.png"&amp;amp;gt;对于小椿的祝福，宫园不假思索就给出了肯定和当然的答案<img src="https://pic2.zhimg.com/e319f589c2dc446fd54265f8d0431311_b.png" class="origin_image zh-lightbox-thumb lazy" width="442" />对于小椿的祝福，宫园不假思索就给出了肯定和当然的答案&amp;amp;lt;img src="https://pic3.zhimg.com/a4a1c621186df2e0edd83dff251555f2_b.png" data-rawwidth="429" data-rawheight="301" class="origin_image zh-lightbox-thumb" width="429" data-original="https://pic3.zhimg.com/a4a1c621186df2e0edd83dff251555f2_r.png"&amp;amp;gt;结局：失败<img src="https://pic3.zhimg.com/a4a1c621186df2e0edd83dff251555f2_b.png" class="origin_image zh-lightbox-thumb lazy" width="429" />结局：失败<br />\r\n所有的问题都回答得太快，公生察觉宫园可能在说谎，但立场上他不能问，也不能说<br />\r\n公生决定第二天单独来，从而开始发现真相&amp;amp;lt;img src="https://pic1.zhimg.com/001d1a9e6fa49562130bace9330c3128_b.png" data-rawwidth="441" data-rawheight="388" class="origin_image zh-lightbox-thumb" width="441" data-original="https://pic1.zhimg.com/001d1a9e6fa49562130bace9330c3128_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic1.zhimg.com/001d1a9e6fa49562130bace9330c3128_b.png" class="origin_image zh-lightbox-thumb lazy" width="441" />———————————————————————————————————————————<br />\r\n<b>第二十三个谎言：卡娜蕾</b><br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/0b7cc6b033d2071e922fe0b45318230a_b.png" data-rawwidth="438" data-rawheight="252" class="origin_image zh-lightbox-thumb" width="438" data-original="https://pic3.zhimg.com/0b7cc6b033d2071e922fe0b45318230a_r.png"&amp;amp;gt;<img src="https://pic3.zhimg.com/0b7cc6b033d2071e922fe0b45318230a_b.png" class="origin_image zh-lightbox-thumb lazy" width="438" />&amp;amp;lt;img src="https://pic3.zhimg.com/20eb683419a23eb1f88a2c9b96c996b2_b.png" data-rawwidth="203" data-rawheight="208" class="content_image" width="203"&amp;amp;gt;卡娜蕾只是公生随手买的糕点<img src="https://pic3.zhimg.com/20eb683419a23eb1f88a2c9b96c996b2_b.png" class="content_image lazy" width="203" />卡娜蕾只是公生随手买的糕点<br />\r\n因为公生除了吃鸡蛋三明治充饥平时并不吃糕点<br />\r\n因为公生现在正在吃卡娜蕾<br />\r\n因为宫园一直不知道公生除鸡蛋三明治外还喜欢吃什么糕点<br />\r\n所以宫园觉得公生喜欢吃卡娜蕾<br />\r\n所以宫园喜欢吃卡娜蕾&amp;amp;lt;img src="https://pic2.zhimg.com/896965fd68cd76cd16242550e9da0391_b.png" data-rawwidth="438" data-rawheight="353" class="origin_image zh-lightbox-thumb" width="438" data-original="https://pic2.zhimg.com/896965fd68cd76cd16242550e9da0391_r.png"&amp;amp;gt;没人给宫园带西点也是谎言，不是公生爆料，根本没人知道宫园家开蛋糕店，而且昨天渡他们的手信就是蛋糕<img src="https://pic2.zhimg.com/896965fd68cd76cd16242550e9da0391_b.png" class="origin_image zh-lightbox-thumb lazy" width="438" />没人给宫园带西点也是谎言，不是公生爆料，根本没人知道宫园家开蛋糕店，而且昨天渡他们的手信就是蛋糕<br />\r\n宫园只喜欢公生带来的食物，不管带来的是什么都会强迫自己吃完，最后也因为没能吃完而遗憾&amp;amp;lt;img src="https://pic4.zhimg.com/f0011c5bb170bd3e21238d01f45ba79f_b.png" data-rawwidth="454" data-rawheight="307" class="origin_image zh-lightbox-thumb" width="454" data-original="https://pic4.zhimg.com/f0011c5bb170bd3e21238d01f45ba79f_r.png"&amp;amp;gt;———————————————————————————————————————————<img src="https://pic4.zhimg.com/f0011c5bb170bd3e21238d01f45ba79f_b.png" class="origin_image zh-lightbox-thumb lazy" width="454" />———————————————————————————————————————————<br />\r\n<b>第二十四个谎言：忘记名字</b><br />\r\n&amp;amp;lt;img src="https://pic3.zhimg.com/50b9db9ebcdb4bb1fed4c83f8b39f00e_b.png" data-rawwidth="580" data-rawheight="420" class="origin_image zh-lightbox-thumb" width="580" data-original="https://pic3.zhimg.com/50b9db9ebcdb4bb1fed4c83f8b39f00e_r.png"&amp;amp;gt;<img src="https://pic3.zhimg.com/50b9db9ebcdb4bb1fed4c83f8b39f00e_b.png" class="origin_image zh-lightbox-thumb lazy" width="580" />&amp;amp;lt;img src="https://pic4.zhimg.com/1cd10d8f5fd91b7f516e9dbf6679ccc7_b.png" data-rawwidth="431" data-rawheight="321" class="origin_image zh-lightbox-thumb" width="431" data-original="https://pic4.zhimg.com/1cd10d8f5fd91b7f516e9dbf6679ccc7_r.png"&amp;amp;gt;昨晚宫园的病情蔓延到脚部，第二天她应该打了封闭恳求医生让她最后一次外出，之后她就一直站在树下等着公生等到下课公生走过来<img src="https://pic4.zhimg.com/1cd10d8f5fd91b7f516e9dbf6679ccc7_b.png" class="origin_image zh-lightbox-thumb lazy" width="431" />昨晚宫园的病情蔓延到脚部，第二天她应该打了封闭恳求医生让她最后一次外出，之后她就一直站在树下等着公生等到下课公生走过来<br />\r\n见到公生后兴奋得满面通红<br />\r\n因为这是最后一次和公生约会了<br />\r\n过于紧张和激动令宫园差点就叫出心中那个一直想说但又不能说出来的名字“有马公生君”<br />\r\n为了掩饰失态再次说谎<br />\r\n———————————————————————————————————————————<br />\r\n<b>第二十五个谎言：埋伏渡</b><br />\r\n&amp;amp;lt;img src="https://pic4.zhimg.com/e760456fe8084bcdfa7bfaa4c31a4313_b.png" data-rawwidth="433" data-rawheight="311" class="origin_image zh-lightbox-thumb" width="433" data-original="https://pic4.zhimg.com/e760456fe8084bcdfa7bfaa4c31a4313_r.png"&amp;amp;gt;也是不想让公生误会，谁都知道三人组只有公生是回家部，渡不是在踢球就是在泡妞<img src="https://pic4.zhimg.com/e760456fe8084bcdfa7bfaa4c31a4313_b.png" class="origin_image zh-lightbox-thumb lazy" width="433" />也是不想让公生误会，谁都知道三人组只有公生是回家部，渡不是在踢球就是在泡妞<br />\r\n宫园的耳朵特写表示她知道公生的答案是什么，她在等着公生帮渡解释<br />\r\nPS：前面亦有一次埋伏渡，此处为第二次，两次不一样的哦<br />\r\n———————————————————————————————————————————<br />\r\n<b>第二十六个谎言：赎罪机会</b><br />\r\n&amp;amp;lt;img src="https://pic1.zhimg.com/6764948ecfd268946fb66213bd7b7f4c_b.png" data-rawwidth="424" data-rawheight="416" class="origin_image zh-lightbox-thumb" width="424" data-original="https://pic1.zhimg.com/6764948ecfd268946fb66213bd7b7f4c_r.png"&amp;amp;gt;最后一次外出机会想和公生约会，但实在找不到借口，因为不能让公生误会<img src="https://pic1.zhimg.com/6764948ecfd268946fb66213bd7b7f4c_b.png" class="origin_image zh-lightbox-thumb lazy" width="424" />最后一次外出机会想和公生约会，但实在找不到借口，因为不能让公生误会<br />\r\n公生也听的莫名其妙<br />\r\n最后只能用公生的大挎包解围&amp;amp;lt;img src="https://pic4.zhimg.com/2805e8de3edbd1f36cee3442040c3637_b.png" data-rawwidth="299" data-rawheight="133" class="content_image" width="299"&amp;amp;gt;PS：个人见解吧，这个算谎言好像有点穿凿附会，我自己也搞不明白说了什么谎。。<img src="https://pic4.zhimg.com/2805e8de3edbd1f36cee3442040c3637_b.png" class="content_image lazy" width="299" />PS：个人见解吧，这个算谎言好像有点穿凿附会，我自己也搞不明白说了什么谎。。<br />\r\n不过接下来的几个谎言都是围绕这次“二人约会”展开的，这个谎言也算是推动情节发展<br />\r\n有利于我们更深层次的理解<br />\r\n—————————————————————————————', '/WebPiano/upload/2017/04/17/58f4d6fd21e4f.jpg', 0, 0, 0, 1492440833, 0, 0, 0);
INSERT INTO `wp_news` (`news_id`, `user_id`, `title`, `content`, `thumb`, `status`, `stick`, `fine`, `create_time`, `update_time`, `commentnum`, `comment`) VALUES
(12, 1, '四月是你的谎言评价', '<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	音乐题材漫画在日本并不鲜见，不乏佳作：描绘一群热血少年在摇滚音乐中挥洒青春的《BECK》、以隐忍的笔触塑造孤独的钢琴家形象的《钢琴之森》、将少男少女的朦胧情愫凝结于爵士乐的《坂道上的阿波罗》等等。而今年刚刚完结的这部《四月是你的谎言》，则从不一样的角度，讲述了一段触动人心的故事。\r\n	</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	故事一开始充满了浓郁的日漫风味，乍一看，还以为是一个老套的青春恋爱故事：十一岁时丧母的钢琴神童有马公生，在此后的三年中再也无法听见钢琴的声音，也因此无法继续弹琴。到了十四岁时，受青梅竹马泽部椿的引见，公生与自由奔放的小提琴手宫园薰相遇。从此，他的人生开始改变……\r\n</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	这样的开头不禁让会让很多人联想到羽海野千花老师的名作《三月的狮子》，甚至就连漫画的名字也有几分相似。同样是才华横溢的男主角在他人的帮助下重新找回生活的意义，逐渐获得成长的故事。而两者不同的是，《三月的狮子》仿佛一条小溪般缓缓流淌，在富有浓郁生活气息的叙述中传达出作者的体悟；而《四月是你的谎言》却如同大海的波浪一般，将所有想要传达的东西在漫画中直接宣泄出来，不断地冲击着读者的心灵。作为一部描绘成长的少年漫画，我想，选择这种方式进行叙述，对青春期的少年少女读者们而言，或许更能引起他们的共鸣。\r\n	</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	个人感觉，这部漫画的整体构架并无太多出众之处，类似的“阳光美少女拯救天才男主角”的桥段早已成为一个固定套路，无论是在漫画还是影视文学上都屡见不鲜。原作者新川直司也承认，这部作品的创作很大程度上受到了日本上世纪流行的青春文学《一五同盟》的影响，我们也不难找到这部漫画与《一五同盟》的种种相似点。不过，尽管《四月是你的谎言》没有什么新颖的故事设定、曲折离奇的情节，但是，这部优秀作品的魅力并不在整体构架上，而在于作者用画面与独白营造出的氛围与描绘出的细节上。激烈的戏剧冲突也许更容易造成感官上的震撼、引发读者阅读兴趣，但是，真正触动人心的往往还是一些细微之处，比如一句台词，一幅画面这样能给人留下无尽回味的东西。而《四月是你的谎言》，在这点上可谓做到了极致。\r\n</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	《四月是你的谎言》的剧情是明暗两条线交织在一起展开的。明线是作为钢琴家的有马公生在小提琴家宫园薰等人的帮助下，克服心魔，重新回到舞台上演奏的故事；暗线是少年有马公生在经历了生活的种种可能性之后最终成长为一个真正的男人的故事。这两条线是互为表里，互为因果的。有马公生的成长需要通过一次次的钢琴演出表现出来，而在这些演出中，有马公生也一定从音乐中领悟到了什么。他所领悟到的东西，正是故事要表达的东西。\r\n	</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	情节上，以有马公生的演出为界限，这部漫画有五个关键的转折点，而每一次转折都意味着有马公生的一次蜕变：在与宫园薰的合奏中，有马公生领略到了不曾体会过的风景，重新对音乐燃起了希望；在与相座武士、井川绘见这两名对手的比赛中，有马公生明白了自己是为宫园薰而演奏；在本应与宫园薰进行的第二次合奏中，有马公生彻底改变了自己心中对母亲的执念，将心魔彻底祛除；在与相座凪的四手联弹中，有马公生将从宫园薰那里得到的激励传递了出去，同时也反过来向消沉的宫园薰传递了力量；而在最后一次演出中，有马公生最终明白，他之所以能够演奏，都是因为有一群人在他身边，给他鼓励，给他支持，而他也应当用音乐回报这些人。有马公生的成长与传统少年漫画中“友情，努力，胜利”的价值观不谋而合。从这点意义上说，《四月是你的谎言》与我们耳熟能详的热血漫画在本质上并无不同：钢琴就是他的武器，舞台就是他的战场。\r\n</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	《四月是你的谎言》的表现手法有几个很明显的特点。首先是内心独白的大量使用。事实上，很多读者认为这种使用方式并不能算成功。就连传统的电影理论也告诉我们，能够使用画面与对白传达的东西，就不要使用画外音，否则，对读者是一种亵渎。个人认为，内心独白的大量使用固然有受作者功力所限的原因，但是，对于这部作品而言，内心独白是不可或缺的。这部作品毕竟是音乐题材，声音难以通过画面传达，一些必要的独白有助于我们理解所要表达的内容。更重要的是，《四月是你的谎言》所要传达的，不仅仅是主人公有马公生的所思所想，更是我们每个人在成长时的共同体验，换句话说，是要引起共鸣。青春的情绪总是复杂的，成长更加难以捉摸，在这时，内心独白就成为了作者与我们对话的窗口，引领我们向作者想要表达的主题上靠拢，帮助我们把自己的想法表达出来。相信在阅读这部作品时，每个人都曾被这些独白所触动，回想起自己的青春时代吧。\r\n	</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	另外一个特点是大量的跨页分镜。新川直司虽然出道较晚，作品不多，但每一部作品的分镜都带有强烈的个人风格，在某些方面可以说是登堂入室，比如对于跨页分镜有近乎于偏执的追求。在下面这幅跨页分镜中，一幅跨页分镜就使用了十几格画格，大面积的黑白渲染，画格没有局限于传统的范式，却丝毫没有将叙事节奏打乱，反而将比赛时的气氛完全烘托出来。将画面拿捏到这种程度的作品，不可多得。\r\n</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	还有一个不得不提的特点，就是回忆的使用恰到好处。在读者们已经形成了“回忆就是拖剧情”这种思维定式时，《四月是你的谎言》却反其道而行之，在故事中穿插了大量的回忆部分。难得的是，每一次回忆的插入都有其不可替代的作用，要么是为了与之后的故事相互照应，要么是为了烘托氛围，要么是为了推动情节发展。从中可以看出，作者对每个细节的处理都是很用心的，结构极其严谨，没有落入所谓的“都合主义”的窠臼之中。\r\n	</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	重点谈一下这部作品的结局。对于结局，在读者们的评价中向来褒贬不一。不过在我看来，其实结局为这部作品增光添彩不少。我觉得，结局可能不是最符合读者意愿的结局，但确实是最适合这部作品的结局。宫园薰的逝去与有马公生母亲的逝去是如此相似，却又那么不同。母亲的逝去使得有马公生无法继续钢琴之路，并一直活在母亲的阴影之中；宫园薰的逝去衬托与证明了有马公生的成长，使有马公生明白，失去了自己最亲爱的人，更要坚强——依然有人陪伴着他，和他一起互相扶持，笑对明天，而不应当沉溺在过去的失落与惆怅中。以此，在故事的最后，有马公生从一个男孩子成长为一个真正的男人。\r\n</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">如此说来，《四月是你的谎言》又不仅仅是一部普通的少年漫画。它承载了太多太多的东西了：热血、励志、青春、友情、亲情……还有，最重要的：对生活的热爱，对未来的坚信。这也是结局的交代上进行了大量的开放式处理的原因——结果并不重要，重要的是要用积极的心态面对生活的一切。而且，作者在结局处理上也没有故意向催泪的方向去引导，因为这绝非是《四月是你的谎言》要表达的东西。我想，作者一定是希望读者们在读完最后几页之后，擦干眼泪，像有马公生一样，向着明天努力前行。\r\n	</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	纵观这部作品，音乐只是一个载体，最终所要讲述的还是一个永恒的主题：“追逐梦想的过程，留有遗憾的青春”。一般意义上讲，这确实是一个略显老套的故事，也存在着这样那样的不足，但是作者讲故事的手法，尤其是对细节的成功把握赋予了这部优秀作品独特的感染力。\r\n</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	成长并非必然如此，却又本应如此。所谓一部少年漫画的内涵，就在这里。\r\n	</p>\r\n<p style="color:#333333;font-family:-apple-system, " font-size:medium;"="">\r\n	如果要用一句话来评价这部作品的话，这部作品应当就是一曲有笑有泪的七彩青春赞歌。\r\n</p>', '/WebPiano/upload/2017/04/17/58f4d853efe35.jpg', 1, 1, 1, 1492441179, 0, 0, 1);

