CREATE TABLE IF NOT EXISTS `wp_video` (
  `video_id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL,
  `author` VARCHAR(50) NOT NULL,
  `info` VARCHAR(255) NOT NULL,
  `address` varchar(100) NOT NULL,
  `play_num` int(11) NOT NULL DEFAULT '0',
  `danmu_num` int(11) NOT NULL DEFAULT '0',
  `status` tinyint(1) NOT NULL DEFAULT '1',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`video_id`),
  KEY `name` (`name`),
  KEY `play_num` (`play_num`),
  KEY `danmu_num` (`danmu_num`),
  KEY `create_time` (`create_time`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

CREATE TABLE IF NOT EXISTS `wp_danmu` (
  `danmu_id` int(11) NOT NULL AUTO_INCREMENT,
  `video_id` int(11) NOT NULL,
  `user_id` varchar(50) NOT NULL,
  `content` VARCHAR(50) NOT NULL,
  `status` tinyint(1) NOT NULL DEFAULT '1',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`danmu_id`),
  KEY `video_id` (`video_id`),
  KEY `user_id` (`user_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

